import 'dotenv/config';
import express, { Application } from 'express';
import path from 'path';
import mongoose from 'mongoose';
import bodyParser from 'body-parser';
import cookieParser from 'cookie-parser';
import logger from './utils/logger';

import routes from './routes';

const app : Application = express();
const port = Number(process.env.PORT);
const databaseUrl = String(process.env.DB_URL);

mongoose.connect(databaseUrl);
const db : mongoose.Connection = mongoose.connection;

app.use(bodyParser.urlencoded( { extended:true }) );
app.use(bodyParser.json());
app.use(cookieParser());
app.use('/static', express.static(path.join(__dirname, '../../build/client/'), { index: false }));
app.use('/api/users', routes.users);
app.use('/*', routes.frontend);

app.listen(port, () =>
{
    logger.info(`Server running at http://localhost:${ port }`);
});

export default app;

import 'dotenv/config';
import { NextFunction, Request, Response } from 'express';
import jwt from 'jsonwebtoken';

const authorize = (req : Request, res: Response, next: NextFunction) =>
{
    const secret = String(process.env.SECRET_TOKEN);

    const token = req.cookies.access_token;

    if(!token)
    {
        return res.sendStatus(403);
    }
    try
    {
        const data = jwt.verify(token, secret);
        res.locals.user = data;

        next();
    }
    catch
    {
        return res.sendStatus(403);
    }
};

export default { authorize };

import User from './User';

export default { user: { model: User } };
export { User };

import 'dotenv/config';
import { Schema, Document, model } from 'mongoose';
import crypto from 'crypto';

const secret = process.env.SECRET_TOKEN;

interface IUser
{
    username: string;
    password: string;
    lastlogin: Date;
    age: number;
    country: string;
}

export interface IUserModel extends IUser, Document
{
    hashPassword(password : any): string;
    comparePassword(loginPassword : any): boolean;
}

const schema : Schema = new Schema<IUserModel>({
    username: { type: String, required: true, unique: true },
    password: { type: String, required: true },
    lastlogin: { type: Date }, 
    age: { type: Number },
    country: { type: String }
},
{
    timestamps: true,
    toJSON:
    {
        transform: (doc, ret) =>
        {
            delete ret.password;
        }
    }
});

schema.methods.hashPassword = (password : string) : string =>
{
    const hash : string = crypto
        .createHmac('sha256', String(secret))
        .update(password)
        .digest('hex');
    return hash;
};

schema.methods.comparePassword = function (loginPassword : string) : boolean
{
    if(this.password !== this.hashPassword(loginPassword))
    {
        return false;
    }

    return true;
};

const userModel = model<IUserModel>('User', schema, 'users');

export default userModel;

import { Router } from 'express';
import controllers from '../../controllers';
import validations from './../../validations';
import middlewares from './../../middlewares';

const router = Router();

router.route('/').post(
    validations.users.password,
    validations.users.passwordConfirmation,
    validations.users.allreadyExits,
    validations.users.username,
    controllers.users.signup
);

router.route('/login').post(
    controllers.users.login
);

router.route('/auth').post(
    middlewares.users.authorize,
    controllers.users.authorize
);

router.route('/logout').post(
    controllers.users.logout
);

export default router;

