import 'dotenv/config';
import jwt from 'jsonwebtoken';

interface IUserData
{
    username: string;
}

const sign = (data : IUserData) =>
{
    const secret = String(process.env.SECRET_TOKEN);

    return jwt.sign(data, secret, { expiresIn: '1800s' });
};

export default { sign };

import logger from './logger';
import accessToken from './accessToken';

export {
    logger,
    accessToken
};

import 'dotenv/config';
import log4js from 'log4js';

const serviceName = String(process.env.SERVICE_NAME);

log4js.configure({
    appenders: { [ serviceName ]: { type: 'stdout' } },
    categories: { default: { appenders: [ serviceName ], level: 'info' } }
});

export default log4js.getLogger(serviceName);

import { body } from 'express-validator';
import User from '../models/User';

const passwordConfirmation = body('passwordConfirmation').custom((value, { req }) =>
{
    if (value !== req.body.password)
    {
        throw new Error('Passwords do not match');
    }

    return true;
});

const allreadyExits = body('username').custom((value) =>
{
    return User.findOne({ username: value }).then(user =>
    {
        if(user)
        {
            return Promise.reject('User already exists!');
        }
    });
});

const password = body('password')
    .isLength({ min: 8, max: 16 })
    .withMessage('Password must be stronger');

const username = body('username')
    .isEmail()
    .withMessage('Username must be an email adress!');

export default {
    passwordConfirmation,
    allreadyExits,
    password,
    username
};
