import React, { useState } from 'react';

import './Notification.scss';

interface INotification
{
    children?: React.ReactNode;
    type: string;
    closable?: boolean;
    key?: number;
}

export = (props : INotification) =>
{
    const [ isClosed, setIsClosed ] = useState(false);

    const handleClose = () =>
    {
        setIsClosed(true);
    };

    const notificationType = (type : string) =>
    {
        switch(type)
        {
        case 'warning':
            return 'warning';
        case 'success':
            return 'success';
        case 'error':
            return 'error';
        case 'info':
        default:
            return 'info';
        }
    };

    return (
        !isClosed &&
        <div className={`Notification ${ notificationType(props.type) }`}>
            {
                props.closable &&
                <span
                    className="close"
                    onClick={ () => handleClose() }
                ></span>
            }
            { props.children }
        </div>
    );
}

import React, { useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import axios, { AxiosError } from 'axios';
import { UserDataContext } from '../UserDataProvider';

export default function Dashboard()
{
    const { user, setUser } = useContext(UserDataContext);

    const navigate = useNavigate();

    useEffect(() =>
    {
        axios({
            method: 'post',
            url: '/api/users/auth',

        }).then((response : any) =>
        {
            setUser({ ...response.data.user, loggedIn: true });

        }).catch((error: AxiosError) =>
        {
            if (error.response?.status === 403)
            {
                navigate('/login');
            }
        });
    }, []);

    return (
        <div>
            <h1>Dashboard</h1>
            <h2>Willkommen auf deinem Dashboard, { user.username }</h2>
        </div>
    );
}

export default function Home()
{
    return (
        <div id="Home">
            <h1>Home</h1>
        </div>
    );
}

